string ROT13(string text)
{
    string encrypted = "";
    int key = 13;
    int alphabet = 26;

    for (char c : text) {
        char new_char;
        if (c >= 'A' && c <= 'Z') {
            new_char = (((c - 'A') + key) % alphabet) + 'A';
        }
        else if (c >= 'a' && c <= 'z') {
            new_char = (((c - 'a') + key) % alphabet) + 'a';
        }
        else {
            new_char = c;
        }
        encrypted += new_char;
    }
    return encrypted;
}

string ROT47(string text)
{
    string encrypted = "";
    int key = 47;
    int ascii_chars = 94;

    for (char c : text) {
        char new_char = (((c - '!') + key) % ascii_chars) + '!';
        encrypted += new_char;
    }
    return encrypted;
}
