#include <vector>
#include <iostream>
using namespace std;

vector<int> piTable(string s)
{
    int n = s.size();
    vector<int> pi(n + 1);
    pi[0] = -1;
    int b = -1;
    for (int i = 1; i <= n; ++i)
    {
        while (b > -1 && s[b] != s[i - 1])
        {
            b = pi[b];
        }
        b++;
        pi[i] = b;
    }
    return pi;
}

void mp(string s, string p, vector<int> &pi)
{
    int pp = -1;
    int b = 0;
    for (int i = 0; i < s.size(); ++i)
    {
        while (b > -1 && p[b] != s[i])
        {
            b = pi[b];
        }
        b += 1;
        if (b < p.size())
        {
            continue;
        }
        pp = i - b + 1;
        cout << pp << ' ';
        b = pi[b];
    }
    if (pp == -1)
    {
        cout << -1 << '\n';
    }
}

int main()
{
    string p, s;

    cin >> p >> s;

    vector<int> pi = piTable(p);
    mp(s, p, pi);

    return 0;
}
